import dayjs from "dayjs";
import axios from 'axios';

var now = dayjs();
now.format();

const tempData = async () => {
  try {
    const response = await axios.get('http://43.200.120.63/api/Node2');
    const data = response.data.map(item => item.temp);
    console.log(response.data);
    console.log(data);
    return data;
  } catch (error) {
    console.error('Error fetching data:', error);
    return [];
  }
}

export const data = {
  labels: [
    now.subtract(6, "day").format("YYYY-MM-DD"),
    now.subtract(5, "day").format("YYYY-MM-DD"),
    now.subtract(4, "day").format("YYYY-MM-DD"),
    now.subtract(3, "day").format("YYYY-MM-DD"),
    now.subtract(2, "day").format("YYYY-MM-DD"),
    now.subtract(1, "day").format("YYYY-MM-DD"),
    now.format("YYYY-MM-DD")
  ],
  datasets: [
    {
      label: 'Data One',
      backgroundColor: '#f87979',
      data: await tempData(), // 데이터를 가져올 때까지 기다립니다.
    }
  ]
};

