import { createApp } from 'vue'
import App from './App.vue'
import 'bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'
import router from './router.js'
import axios from 'axios'
createApp(App).use(router).mount('#app')
axios.get('http://43.200.120.63/api/Node1').then((response) => {
    // 데이터 가져오기
    const data = response.data.data; // 'data' 배열을 변수에 저장

    // 데이터의 각 요소에서 필요한 값을 변수로 추출
    const tempArray = data.map(item => item.temp); // 'temp' 값을 배열로 추출
    const takArray = data.map(item => item.tak);   // 'tak' 값을 배열로 추출
    const phArray = data.map(item => item.ph);     // 'ph' 값을 배열로 추출
    const doArray = data.map(item => item.do);     // 'do' 값을 배열로 추출

    // 추출한 값을 변수로 사용할 수 있음
    console.log('Temperature Array:', tempArray);
    console.log('Tak Array:', takArray);
    console.log('pH Array:', phArray);
    console.log('DO Array:', doArray);
}).catch((error) => {
    console.error('Error:', error);
});
axios.get('http://43.200.120.63/api/Node2').then((data2) => {
    console.log(data2)
})
axios.get('http://43.200.120.63/api/Node3').then((data3) => {
    console.log(data3)
})
