// store.js
import { createStore } from 'vuex';

export default createStore({
  state: {
    chartData: {
      lineChartData: [],
      lineChartData2: [],
      lineChartData3: [],
      lineChartData4: [],
    },
  },
  mutations: {
    updateChartData(state, data) {
      state.chartData = data;
    },
  },
});
